    public static ArrayList<Movie> getGenreMovie(String tabGenre){
        ArrayList<Movie>movies=new ArrayList<>();
        for(Movie movie:getAllMovies()){
            if(movie.getGenre().equals(tabGenre)){
                movies.add(movie);
            }
        }
        return movies;
    }
    public static String []getAllMovieNames(){
        ArrayList<Movie> allMovies=getAllMovies();
        String []arrMovie=new String[allMovies.size()];
        for(int i=0;i<allMovies.size();i++){
            arrMovie[i]=allMovies.get(i).getName();
        }
        return arrMovie;
    }

    public static Movie findMovieByName(String queryString) {
        for(Movie movie:getAllMovies()){
            if(movie.getName().equals(queryString)){
                return movie;
            }
        }
        return new Movie();
    }
